
public class Variable01P30 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 변수는 일종의 박스입니다.
		// 이 박스에는 넣을 물건의 종류와 박스 명칭을 부여해야합니다.
		
		// 아래는 "정수"라는 물건이 a라는 이름의 박스에 담길 예정이라는 선언입니당
		int a;
		// a박스에 5를 집어넣습니다.
		a = 5;
		int b;
		b = 6;
		// a박스에 할당된 값을 콘솔에 찍어봅시당
		System.out.println(a + b);
		
		// 정수를 담을 수 있는 A라는 박스를 만들어주세용.
		int A;
		// A박스에 10을 넣어주세요.
		A = 10;
		// A박스에 들어있던 값을 콘솔에 찍어주세요.
		System.out.println(a + A);

	}

}
